                             and
                              CASE 
							 WHEN P_location is not null AND useraccountprofile.Location like CONCAT('%',P_location,'%') THEN 1
							 WHEN P_location is null AND useraccountprofile.Location = useraccountprofile.Location THEN 1
						     END=1 
